#define ARIBB24_NO_ERROR 0#define ARIBB24_NO_PES_PACKET 1#define ARIBB24_NO_ARIB_PACKET 2#define ARIBB24_NO_VALID_DATA_GROUP 3#define ARIBB24_INVALID_DATA_GROUP_LENGTH 4#define ARIBB24_DATAGROUP_CAPTION_MANAGEMENT 0#define ARIBB24_DATAGROUP_CAPTION_STATEMENT 1struct TS_header {	uint16_t PID;	uint8_t PUSI;	uint8_t AFC;};struct ARIB_data_unit {	uint32_t size;	uint8_t parameter;	uint8_t* payload;};struct ARIB_data_group {	uint8_t id;	uint16_t size;	uint8_t type;	uint8_t hasOffsetTime;	uint8_t* offsetTime;	uint8_t languages_num;	char** languages;	uint32_t data_units_num;	struct list_element* data_units;};struct PES_info {	struct TS_header *header;	struct ARIB_data_group *datagroup;	uint8_t isPES;	uint8_t isARIBB24;	int exit_code;		uint8_t SID;	uint8_t PPL;	uint8_t PTSDTSF;	uint8_t* DTS;	};